import React, { useEffect, useState } from 'react';
import { getQuestions } from '../api';
import he from 'he';

export const Questions = (props) => {
  const [expanded, setExpanded] = useState(false)
  const [questions, setQuestions] = useState({})
  const [loading, setLoading] = useState(true)

  const { selectedCategory, setSelectedCategory } = props
  const handleExpanded = () => {
    setExpanded(!expanded)
  };

  useEffect(() => {
    getQuestions(selectedCategory).then((data) => {
      setQuestions(data)
      setLoading(false)
    })
  }, [selectedCategory])

  return loading ? (
    'Questions are loading'
  ) : (
    <>
      <div className=''>
        <button
          className='button is-primary'
          onClick={() => setSelectedCategory(null)}
        >
          Back to all Categories
        </button>
        <h2>{selectedCategory.name}</h2>
      </div>
      {questions.map((question) => {
        return (
          <div key={question.question} class='tile is-ancestor'>
            <div key={question.question} class='tile is-4 is-parent'>
              <div key={question.question} class='tile is-child box'>
                <p>{he.decode(question.question)}</p>
                <p>Select your answer:</p>
                <div>
                  {question.incorrect_answers.map((data) => {
                    return (
                      <button
                        className='guess'
                        class='button is-primary is-light is-rounded'
                      >
                        {data.incorrect_answers}
                      </button>
                    )
                  })}
                  <button
                    className='guess'
                    class='button is-primary is-light is-rounded'
                  >
                    {data.correct_answer}
                  </button>
                </div>
                {expanded ? 'Hide Answer' : 'Show Answer'}
                <button
                  onClick={handleExpanded}
                  class='card-header-icon'
                  aria-label='more options'
                >
                  <span class='icon'>
                    <i class='fas fa-question-circle' />
                  </span>
                </button>
                {expanded && (
                  <>
                    <p>{data.correct_answer}</p>
                  </>
                )}
              </div>
            </div>
          </div>
        )
      })}
    </>
  )
};
