import { getQuestions } from '../api'

export const Questions = (props) => {
  const [questions, setQuestions] = useState({})
  const [loading, setLoading] = useState(true)

  const { selectedCategory } = props

  useEffect(() => {
    getQuestions(selectedCategory).then(data => {
      setQuestions(data)
